  return (
    <div className="bg-gray-100">
      {/* <div className = "text-4xl text-center font-bold text-blue-500">Hello Tailwind</div> */}
      <div className = "px-8 py-16">
        <img className = "h-40" src = {img2} alt = "svgimage" />
        <img className="h-40 mt-4 rounded-xl shadow-xl" src = {img1} alt = "svgimage" />
        <h1 className="mt-4 text-2xl font-bold text-black-900">You can work from anywhere.
        <span className="text-indigo-500"> Take advantage of it</span> </h1>
      </div>
    </div>
  );
}

export default App;
