import { createSlice } from '@reduxjs/toolkit';
import { stat } from 'fs-extra';
// import postData from './fakeApiData';



export const initialState = {
	loading: false,
	hasErrors: false,
	posts: [],
}

const postsSlice = createSlice({
	name: 'posts',
	initialState,
	reducers: {
		getPosts: state => {
			state.loading = true;
		},
		getPostsSuccess: (state, {payload}) => {
			state.posts = payload
			state.loading = false
			state.hasErrors = false
		},
		getPostsFailure: state => {
			state.loading = false
			state.hasErrors = true
		}
	}
});

// Asynchronous thunk function
export function fetchPosts() {
	return async dispatch => {
		dispatch(getPosts());

		// try {
		// 	const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
		// 	const data = await response.json()

		// 	dispatch(getPostsSuccess(data.meals))
		//   } catch (error) {
		// 	dispatch(getPostsFailure())
		//   }

		try {

			const postsData = postData;
			dispatch(getPostsSuccess(postsData));

		} catch(error) {
			dispatch(getPostsFailure());
		}
	}
}

// three action generated from this slice
export const { getPosts, getPostsSuccess, getPostsFailure } = postsSlice.actions;

// a selector
export const postsSelector = state => state.posts;

// the reducer
export default postsSlice.reducer;
