// "build": "vite build", inside package.json

import logo from './logo.svg';
import img1 from './assets/img1.jpg';
import img2 from './assets/img2.png';
import { RiShareForwardLine } from 'react-icons/ri'
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import './App.css';


function ReactionButton({reactionText}) {
  return (
    <button className="rounded-full py-2 px-3">
      {reactionText}
    </button>
  );
}

function ReactionSection() {
  return (
    <div className="grid-flow-row text-xs">
      {/* <ReactionButton reactionText="15 Good" />
      <ReactionButton reactionText="7 Awesome"/>
      <ReactionButton reactionText="5 Excellent"/>
      <ReactionButton reactionText="1 Bad"/> */}
      <button className="bg-yellow-500 rounded-full py-2 px-3 mr-2 mt-1">
        15 Good
      </button>
      <button className="bg-yellow-400 rounded-full py-2 px-3 mr-2 mt-1">
        7 Awesome
      </button>
      <button className="bg-yellow-300 rounded-full py-2 px-3 mr-2 mt-1">
        5 Excellent
      </button>
      <button className="bg-yellow-200 rounded-full py-2 px-3 mr-2 mt-1">
        1 Bad
      </button>
    </div>
  );
}

function App() {

  return (
    <div>
      <div className="grid gap-1 h-full grid-cols-1 sm:grid-cols-4">
        <div className="bg-yellow-500 col-span-1 order-2 sm:order-1">Col 1</div>
        <div className="bg-gray-100 col-span-2 order-1">
          <div className=" bg-white m-2 rounded-3xl border-2 border-yellow-500 shadow-lg">
            <div className="grid grid-flow-row">
              <div className="flex justify-items-stretch ml-6 mt-6 mb-5">
                <img src={img1} className="w-14 h-14 rounded-full object-cover object-cente"/>
                <div className="flex-col mt-1 ml-4">
                  <div className="text-lg font-bold">
                    Steve Hawk
                  </div>
                  <div className="text-base text-gray-500">
                    28 jun 2020
                  </div>
                </div>
                <div className = "ml-auto mr-6">
                  <RiShareForwardLine size = {32} onClick = {() => {}}/>
                </div>
              </div>
              <div className="mt-0 ml-14 font-medium text-lg">
                description
              </div>
              <div className="mt-2">
                {/* <img src = {img1} className="" /> */}
                <AwesomeSlider bullets = {false}> 
                  <div className = "postImage" data-src="https://live.staticflickr.com/65535/51289414298_52b007f540_h.jpg" />
                  <div className = "postImage" data-src="https://live.staticflickr.com/65535/51294202574_2bfe8c0cdb_h.jpg" />
                </AwesomeSlider>

              </div>
              <div className="ml-4 mt-3 mb-4">
                <ReactionSection />
              </div>
            </div>
          </div>
        </div>
        <div className="bg-blue-500 order-3 sm:order-1">
          <div className="grid-flow-col align-ite">
            <div className="bg-yellow-500 w-32 h-32 rounded-lg m-2 content-center align-middle">
            </div>
            <div className="bg-yellow-500 w-32 h-32 rounded-lg m-2 content-center align-middle">
            </div>
          </div>

        </div>   
        {/* <div className="bg-blue-900">Col 4</div>     */}
      </div>
    </div>

    // <div className="bg-gray-100 m-2 rounded-lg shadow-lg">
    //   <div className="grid lg:grid-cols-2">
    //   <div className="px-8 py-16 max-w-md mx-auto sm:max-w-xl lg:px-12 lg:py-24 lg:max-w-full">
