import img1 from '../assets/img1.jpg';
import img3 from '../assets/img3.jpg';

function UserProfile() {

	function openCity(e, cityName) {

		e.preventDefault();

		var i, tabcontent, tablinks;
		tabcontent = document.getElementsByClassName("tabcontent");
		for (i = 0; i < tabcontent.length; i++) {
			tabcontent[i].style.display = "none";
		}
		tablinks = document.getElementsByClassName("tablinks");
		for (i = 0; i < tablinks.length; i++) {
			tablinks[i].className = tablinks[i].className.replace(" active", "");
		}
		document.getElementById(cityName).style.display = "block";
		evt.currentTarget.className += " active";
	}

	return (
		<div className="m-auto w-8/12">
			<div className=" relative bg-gray-200 m-2 rounded-lg">
				<div className="flex-col">
					<div className="relative">
						<img src={img1} className="h-64 w-max rounded-t-lg object-cover object-center" />
						<div className="pl-6">
							<img src={img3} className=" absolute top-2/3 w-32 h-32 rounded-full border-4 border-white object-cover object-center"/>
						</div>
					</div>
					<div>  {/* name and tag*/}
						<div className="mt-12 ml-10 text-xl font-bold">
							Jane Jone
						</div>
						<div className="ml-9 text-sm">
							@jane_jone
						</div>
					</div>
					<div className="ml-9 mt-4">
						Passionte learning new technologies. <section className="inline text-yellow-500">#Motivation</section> comes from the heart and never give up. If you fail stand up and try again. <section className="inline text-yellow-500">#NeverGiveUp</section>
					</div>
					<div className="ml-9 mt-4">
						<button className="mr-12" onClick={openCity(event, 'Posts')}>POSTS</button>
						<button className="mr-12">BOOKMARKS</button>
						<button className="mr-12">OTHERS</button>
					</div>
				</div>
				</div>
		</div>
	);
}

function TabContent() {
	return (
		<div>
			<h3>Posts</h3>
		</div>
	);
}

export default UserProfile;
