                    <ReactionButton reactionText="15 Good" />
                  </div>
                  <div className="bg-yellow-400">
                    <ReactionButton reactionText="7 Awesome"/>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="bg-blue-500">Col 3</div>   
        {/* <div className="bg-blue-900">Col 4</div>     */}
