import logo from './logo.svg';
import img1 from './assets/img1.jpg';
import img2 from './assets/img2.png';
import './App.css';

function App() {
  return (
    <div className="bg-gray-100">
      <div className="grid grid">
      <div className="px-8 py-16 max-w-md mx-auto sm:max-w-xl">
        {/* <img className = "h-40" src = {img2} alt = "svgimage" /> */}
        <img className="h-auto w-auto mt-4 rounded-xl shadow-xl sm:h-64 sm:w-full sm:object-cover object-center lg:hidden" src = {img1} alt = "svgimage" />
        <h1 className="mt-4 text-2xl font-bold text-gray-900 sm:mt-8 sm:text-4xl">You can work from anywhere.
        <span className="text-indigo-500 "> Take advantage of it</span> </h1>
        <p className="mt-4 text-gray-600 sm:text-xl">Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
          Morbi gravida pulvinar lorem imperdiet hendrerit. 
          Morbi tristique nisl eget ante posuere vulputate. 
          In scelerisque consectetur turpis.
        </p>
        <div className="mt-4">
          <a className="text-white bg-indigo-500 px-2 py-2 inline-block rounded-lg shadow-lg uppercase tracking-wider font-semibold sm:text-xl" href = "#" >Click Here</a>
        </div>
      </div>
      <img className="hidden lg:block" src = {img1} alt = "svgimage" />
      </div>
    </div>
  );
}

export default App;
