import React from 'react';
import './side_drawer.css';

function SideDrawer({show}) {

	let drawerClasses = "sideDrawer";
	if(show) {
		drawerClasses = "sideDrawer open";
	}

	return (
		<nav className={`${drawerClasses} bg-white h-full w-4/6 fixed shadow-lg top-0 left-0 z-20`}>
			<ul className="flex flex-col justify-center p-4 h-full list-none">
				<li className="hover:text-yellow-500 active:bg-yellow-500">
						<button className="flex items-center ml-20 px-2 hover:bg-gray-100   rounded-md" onClick={() => setSelectedNav('home')}>

					{selectedNav ? <HomeWorkRounded style={{fontSize: 42, color:"rgba(245, 158, 11)"}} /> : 	<HomeWorkRounded style={{ fontSize: 42}} />}

			<div className={`font-face-gm text-xl ${selectedNav ? "text-yellow-500" : ""}`}>
				Home
			</div>
			</button>
				</li>
				<li><a href="/" ></a>Users</li>
			</ul>
		</nav>
	);
}

export default SideDrawer;
